INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (1,'Access Control and Identity Verification','Apply a broad range of physical, technological, and cyber measures to control admittance to critical locations and systems, limiting access to authorized individuals to carry out legitimate activities.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (2,'Community Resilience','Lead the integrated effort to recognize, understand, communicate, plan, and address risks so that the community can develop a set of actions to accomplish Mitigation and improve resilience.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (3,'Critical Transportation','Provide transportation (including infrastructure access and accessible transportation services) for response priority objectives, including the evacuation of people and animals, and the delivery of vital response personnel, equipment, and services into the affected areas.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (4,'Cybersecurity','Protect against damage to, the unauthorized use of, and/or the exploitation of (and, if needed, the restoration of) electronic communications systems and services (and the information contained therein).');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (5,'Economic Recovery','Return economic and business activities (including food and agriculture) to a healthy state and develop new business and employment opportunities that result in a sustainable and economically viable community.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (6,'Environmental Response/Health and Safety','Ensure the availability of guidance and resources to address all hazards including hazardous materials, acts of terrorism, and natural disasters in support of the responder operations and the affected communities.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (7,'Fatality Management Services','Provide fatality management services, including body recovery and victim identification, working with state and local authorities to provide temporary mortuary solutions, sharing information with mass care services for the purpose of reunifying family members and caregivers with missing persons/remains, and providing counseling to the bereaved.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (8,'Forensics and Attribution','Conduct forensic analysis and attribute terrorist acts (including the means and methods of terrorism) to their source, to include forensic analysis as well as attribution for an attack and for the preparation for an attack in an effort to prevent initial or follow-on acts and/or swiftly develop counter-options.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (9,'Health and Social Services','Restore and improve health and social services networks to promote the resilience, independence, health (including behavioral health), and well-being of the whole community.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (10,'Housing','Implement housing solutions that effectively support the needs of the whole community and contribute to its sustainability and resilience.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (11,'Infrastructure Systems','Stabilize critical infrastructure functions, minimize health and safety threats, and efficiently restore and revitalize systems and services to support a viable, resilient community.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (12,'Intelligence and Information Sharing','Provide timely, accurate, and actionable information resulting from the planning, direction, collection, exploitation, processing, analysis, production, dissemination, evaluation, and feedback of available information concerning threats to the United States, its people, property, or interests; the development, proliferation, or use of WMDs; or any other matter bearing on U.S. national or homeland security by Federal, state, local, and other stakeholders. Information sharing is the ability to exchange intelligence, information, data, or knowledge among Federal, state, local, or private sector entities, as appropriate.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (13,'Interdiction and Disruption','Delay, divert, intercept, halt, apprehend, or secure threats and/or hazards.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (14,'Long-term Vulnerability Reduction','Build and sustain resilient systems, communities, and critical infrastructure and key resources lifelines so as to reduce their vulnerability to natural, technological, and human-caused incidents by lessening the likelihood, severity, and duration of the adverse consequences related to these incidents.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (15,'Mass Care Services','Provide life-sustaining services to the affected population with a focus on hydration, feeding, and sheltering to those who have the most need, as well as support for reunifying families.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (16,'Mass Search and Rescue Operations','Deliver traditional and atypical search and rescue capabilities, including personnel, services, animals, and assets to survivors in need, with the goal of saving the greatest number of endangered lives in the shortest time possible.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (17,'Natural and Cultural Resources','Protect natural and cultural resources and historic properties through appropriate planning, mitigation, response, and recovery actions to preserve, conserve, rehabilitate, and restore them consistent with post-disaster community priorities and best practices and in compliance with appropriate environmental and historical preservation laws and executive orders.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (18,'On-scene Security and Protection','Ensure a safe and secure environment through law enforcement and related security and protection operations for people and communities located within affected areas and also for all traditional and atypical response personnel engaged in lifesaving and life-sustaining operations.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (19,'Operational Communications','Ensure the capacity for timely communications in support of security, situational awareness, and operations by any and all means available, among and between affected communities in the impact area and all response forces.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (20,'Operational Coordination','Establish and maintain a unified and coordinated operational structure and process that appropriately integrates all critical stakeholders and supports the execution of core capabilities.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (21,'Physical Protective Measures','Reduce or mitigate risks, including actions targeted at threats, vulnerabilities, and/or consequences, by controlling movement and protecting borders, critical infrastructure, and the homeland.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (22,'Planning','Conduct a systematic process engaging the whole community as appropriate in the development of executable strategic, operational, and/or community-based approaches to meet defined objectives.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (23,'Public and Private Services and Resources','Provide essential public and private services and resources to the affected population and surrounding communities, to include emergency power to critical facilities, fuel support for emergency responders, and access to community staples (e.g., grocery stores, pharmacies, and banks) and fire and other first response services.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (24,'Public Health and Medical Services','Provide lifesaving medical treatment via emergency medical services and related operations and avoid additional disease and injury by providing targeted public health and medical support and products to all people in need within the affected area.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (25,'Public Information and Warning','Deliver coordinated, prompt, reliable, and actionable information to the whole community through the use of clear, consistent, accessible, and culturally and linguistically appropriate methods to effectively relay information regarding any threat or hazard, as well as the actions being taken and the assistance being made available, as appropriate.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (26,'Risk and Disaster Resilience Assessment','Assess risk and disaster resilience so that decision makers, responders, and community members can take informed action to reduce their entity''s risk and increase their resilience.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (27,'Risk Management for Protection Programs and Activities','Identify, assess, and prioritize risks to inform Protection activities and investments.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (28,'Screening, Search, and Detection','Identify, discover, or locate threats and/or hazards through active and passive surveillance and search procedures. This may include the use of systematic examinations and assessments, sensor technologies, or physical investigation and intelligence.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (29,'Situational Assessment','Provide all decision makers with decision-relevant information regarding the nature and extent of the hazard, any cascading effects, and the status of the response.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (30,'Supply Chain Integrity and Security','Strengthen the security and resilience of the supply chain.');
INSERT INTO `core_capability` (`id`, `name`, `description`) VALUES (31,'Threats and Hazard Identification','Identify the threats and hazards that occur in the geographic area; determine the frequency and magnitude; and incorporate this into analysis and planning processes so as to clearly understand the needs of a community or entity.');
